int CalcSize(vector<vector<P> > &range){
	int result=0;
	REP(x,0,10010){
		int index=0,y=0;
		while(index<range[x].size()){
			for(y=max(y,range[x][index].F_); y<range[x][index].S_; ++y) ++result;
			++index;
		}
	}
	return result;
}

void Add(vector<vector<P> > &range,vector<vector<unsigned char> > &cnt,int x,int &result){
	int index=0,y=0;
	while(index<range[x].size()){
		for(y=max(y,range[x][index].F_); y<range[x][index].S_; ++y){
			if(cnt[(x-1)%3][y]==0) ++result;
			if(cnt[(x+1)%3][y]==0) ++result;
			if(cnt[x%3][y-1]==0) ++result;
			if(cnt[x%3][y+1]==0) ++result;
		}
		++index;
	}
}

int CalcLength(vector<vector<P> > &range){
	int result=0;
	vector<vector<unsigned char> > cnt(3,vector<unsigned char>(10010));
	REP(x,1,10009){
		if(x-2>=0) fill(cnt[(x-2)%3].begin(),cnt[(x-2)%3].end(),0);
		int index=0,y=0;
		while(index<range[x+1].size()){
			for(y=max(y,range[x+1][index].F_); y<range[x+1][index].S_; ++y) ++cnt[(x+1)%3][y];
			++index;
		}
		Add(range,cnt,x,result);
	}
	return result;
}

