            q[2*i] = manip(ys, xs, xt, 1);
            q[2*i +1] = manip(yt, xs, xt, -1);
        }
        sort(q, q +2*n);
        q[2*n].y = 1e9;

        int area = 0;
        int outlen = 0;
        int pt = 0;
        for(int i=0; i<size; i++){
            memcpy(field[i%2], field[1-i%2], sizeof(int)*size/*sizeof(field[0])*/);
            bool changed = false;
            int diff[10003] = {};
            while(q[pt].y == i){
                changed = true;
                diff[q[pt].xs] += q[pt].val;
                diff[q[pt].xt] -= q[pt].val;
                pt++;
            }
            if(changed){
                for(int j=0; j<size -1; j++){
                    diff[j+1] += diff[j];
                    field[i%2][j] += diff[j];
                }
            }
            for(int j=0; j<size -1; j++){
                if(field[i%2][j] > 0) area++;
                if((field[i%2][j]==0) != (field[i%2][j+1]==0)) outlen++;
                if((field[0][j]==0) != (field[1][j]==0)) outlen++;
            }
        }
        printf("%d\n", area);
        if(r==2){
            printf("%d\n", outlen);
        }
    }
    return 0;
}

