                        ++len;
                }
                else
                {
                    if (cur[x - 1] == 0)
                        ++len;
                    if (prev[x] == 0)
                        ++len;
                    ++area;

                    max_swap(cur[x + 1], cur[x] - 1);
                }
            }
        }

        if (r == 1)
            printf("%d\n", area);
        else
            printf("%d\n%d\n", area, len);
    }
}

