int main() {
  while(1){
    int n,r;
    cin>>n>>r;
    if(!(n||r))break;
    vector<vector<pair<int,int>>> v(10001);
    REP(i,n){
      int x1,y1,x2,y2;
      cin>>x1>>y1>>x2>>y2;
      v[x1].emplace_back(y1,1);
      v[x1].emplace_back(y2,-1);
      v[x2].emplace_back(y1,-1);
      v[x2].emplace_back(y2,1);
    }
    REP(i,10001)sort(begin(v[i]),end(v[i]));
    int area = 0;
    int circ = 0;
    vector<int> val(10001,0);
    REP(i,10001){
      vector<int> tmp(10001);
      int sum = 0;
      int k=0;
      REP(j,10001){
        while(k<v[i].size()&&v[i][k].first<j)k++;
        while(k<v[i].size()&&v[i][k].first==j){
          sum += v[i][k].second;
          k++;
        }
        tmp[j] = val[j] + sum;
        if(tmp[j]>0)++area;
        if((tmp[j] > 0 && val[j] == 0) || (tmp[j] == 0 && val[j] > 0))
          circ++;
        if(j>0&&((tmp[j]==0&&tmp[j-1]>0)||(tmp[j]>0&&tmp[j-1]==0)))
          circ++;
        if(j==0&&tmp[j]>0)
          circ++;
      }
      swap(tmp,val);
    }
    cout << area << endl;
    if(r==2)
      cout << circ << endl;
  }
