#define FOR(i,a,b) for(int i=(a);i<(b);i++)
#define REP(i,j) FOR(i,0,j)

const int INF = 1 << 24;
const int dx[4] = {0, 0, 1, -1}, dy[4] = {1, -1, 0, 0};

typedef unsigned long long ull;

int N, R, min_x, max_x, min_y, max_y;
int R_2[10010][10010];

int get(int x, int y){
    if(0 <= x && x <= 10000 && 0 <= y && y <= 10000){
        return R_2[x][y];
    }
    return 0;
}

int solve_S(){
    int res = 0;
    FOR(i, min_y, max_y+1){
        FOR(j, min_x, max_x+1){
            if(R_2[j][i] > 0){
                res++;
            }
        }
    }

    return res;
}

int solve_L(){
    int res = 0;
    FOR(i, min_y, max_y+1){
        FOR(j, min_x, max_x+1){
            if(R_2[j][i] > 0){
                res += !get(j-1, i) + !get(j+1, i) + !get(j, i-1) + !get(j, i+1);
            }
        }
    }

    return res;
}

int main(){
    while(std::cin >> N >> R, N){
        min_x = 10000;
        max_x = 0;
        min_y = 10000;
        max_y = 0;

        REP(i, 10001){
            REP(j, 10001){
                R_2[j][i] = 0;
            }
        }

        REP(i, N){
            int x1, y1, x2, y2;
            std::cin >> x1 >> y1 >> x2 >> y2;

            R_2[x1][y1]++;
            R_2[x1][y2]--;
            R_2[x2][y1]--;
            R_2[x2][y2]++;

            min_x = std::min(min_x, x1);
            max_x = std::max(max_x, x2);
            min_y = std::min(min_y, y1);            
            max_y = std::max(max_y, y2);
        }

        // X
        FOR(i, min_y, max_y+1){
            FOR(j, min_x+1, max_x+1){
                R_2[j][i] += R_2[j-1][i];
            }
        }

        // Y
        FOR(i, min_x, max_x+1){
            FOR(j, min_y+1, max_y+1){
                R_2[i][j] += R_2[i][j-1];
            }
        }

        if(R == 1){
            std::cout << solve_S() << std::endl;
        }else{
            std::cout << solve_S() << std::endl;
            std::cout << solve_L() << std::endl;
        }
    }
}

