  for(i=0;i<Mx;i++){
    for(j=0;j<My;j++){
      f[i][j] = 0;
    }
  }
  for(k=0;k<n;k++){
    for(i=p[k][0];i<p[k][2];i++){
      for(j=p[k][1];j<p[k][3];j++){
        if(f[i][j]==0){
          s++;
        }
        f[i][j] = 1;
      }
    }
  } 
  printf("%d\n",s);
  if(r==2){
    for(i=0;i<Mx-1;i++){
      l+=(f[i][0]+f[i][Mx-1]);
      for(j=0;j<My-1;j++){
        l+=(f[i][j]^f[i][j+1]);
      }
    }
    for(j=0;j<My-1;j++){
      l+=(f[0][j]+f[My-1][j]);
      for(i=0;i<Mx-1;i++){
        l+=(f[i+1][j]^f[i][j]);
      }
    }
    printf("%d\n",l);
