    for(int i=0;i+1<10001;i++){
      v.clear();
      for(int j=0;j<n;j++){
	if(in[j].S.S>=i+1 && i>=in[j].S.F){
	  v.push_back(make_pair(in[j].F.F,in[j].F.S));
	}
	if(i<in[j].S.F)break;
      }

      if(v.size()!=0){
	sort(v.begin(),v.end());
