      rangeSet[y2].push_back(make_pair(pii(x1,x2),-1));
      maxy=max(y2+1,maxy);
      maxx=max(x2,maxx);
      miny=min(y1,miny);
      minx=min(x1,minx);
    }
    int S=0;
    int E=0;
    int cur=miny%2;
    int prv=cur^1;
    for(int y=miny;y<maxy;y++){
