	  S++;
	  field[cur][x]=1;
	}
      }
      for(int x=minx;x<maxx;x++){
	if(field[prv][x]^field[cur][x])E++;
	if(field[cur][x]==1){
	  if(x-1<0||field[cur][x-1]==0)E++;
	  if(field[cur][x+1]==0)E++;
	}
      }
      swap(cur,prv);
    }
