inline void Imos(vector<vector<unsigned char> > &plane){
	REP(y,0,plane[0].size()){
		REP(x,1,plane.size()){
			plane[x][y]+=plane[x-1][y];
		}
	}
	REP(x,0,plane.size()){
		for(int y=plane[0].size()-2; y>=0; --y){
			plane[x][y]+=plane[x][y+1];
		}
	}
}

inline int CalcSize(vector<vector<unsigned char> > &plane){
	int result=0;
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(plane[x][y]>0) ++result;
		}
	}
	return result;
}

inline int BFS(vector<vector<unsigned char> > &plane,vector<vector<bool> > &has_done,int x,int y,int W,int H){
	int result=0;
	queue<P> que;
	que.push(MP(x,y));
	has_done[x][y]=true;
	while(!que.empty()){
		P p=que.front();
		que.pop();
		REP(i,0,4){
			int nx=p.F_+dx[i],ny=p.S_+dy[i];	
			if(0<=nx&&nx<W&&0<=ny&&ny<H&&!has_done[nx][ny]){
				if(plane[nx][ny]>0){
					que.push(MP(nx,ny));
					has_done[nx][ny]=true;
				}else ++result;
			}
		}
	}
	return result;
}

inline int CalcLength(vector<vector<unsigned char> > &plane,int n,vector<int> &X,vector<int> &Y){
	vector<vector<bool> > has_done(plane.size(),vector<bool>(plane[0].size()));
	int result=0;
	/*
