		REP(y,0,plane[0].size()){
			if(!has_done[x][y]&&plane[x][y]>0){
				result+=BFS(plane,has_done,x,y,plane.size(),plane[0].size());
			}
		}
	}
	*/
	REP(i,0,n){
		if(!has_done[X[i]][Y[i]]&&plane[X[i]][Y[i]]>0) result+=BFS(plane,has_done,X[i],Y[i],plane.size(),plane[0].size());
	}
	return result;
}

