                if(node[w]>0){
                   S++;
                   deltaS++;
                   if(w==0){L+=2;deltaL+=2;}
                   else if(node[w-1]==0){L+=2;deltaL+=2;}
                   if(h==0){L+=2;}
                   else if(map_before[w]==0){L+=2;}
                }


            }
            //cout<<endl;

