#include<iostream>
#include<vector>
#include<set>
#include<algorithm>
using namespace std;

struct Rect {int left, bottom, right, top;};

enum {IN, OUT};
struct Node {
    int y;
    bool in;
    Node(int y, bool in) :y(y), in(in) {}
    bool operator<(const Node& n) const {return y != n.y ? y < n.y : in > n.in;}
};

int main() {
    int n, R;
    while(cin >> n >> R, n | R) {
        vector<Rect> rect(n);
        for(auto& r: rect) cin >> r.left >> r.bottom >> r.right >> r.top;

        set<int> line;
        for(const auto& r: rect) {
            line.insert(r.left);
            line.insert(r.right);
        }

        int area, circumference;
        int edge, touch, pre_x;
        area = circumference = 0;
        edge = touch = pre_x = 0;
        for(const auto& l: line) {
            multiset<Node> node;
            for(const auto& r: rect) {
                if(l < r.left || r.right <= l) continue;
                node.insert(Node(r.bottom, IN));
                node.insert(Node(r.top, OUT));
            }

            area += touch * (l - pre_x);
            circumference += edge * (l - pre_x);
            pre_x = l;

            edge = touch = 0;
            int in, pre_y;
            in = pre_y = 0;
            for(const auto& n: node) {
                if(!in) {
                    pre_y = n.y;
                    ++edge;
                }
                in += n.in ? 1 : -1;
                if(in == 0) ++edge;
                touch += n.y - pre_y;
                pre_y = n.y;
            }

            enum POS {LEFT, MIDDLE, RIGHT};
            struct NodeV {
                int y;
                bool in;
                POS pos;
                NodeV(int y, bool in, POS pos) :y(y), in(in), pos(pos) {}
                bool operator<(const NodeV& n) const {return y != n.y ? y < n.y : in > n.in;}
            };
            multiset<NodeV> node_v;
            for(const auto& r: rect) {
                if(l < r.left || r.right < l) continue;
                if(r.left < l && l < r.right) {
                    node_v.insert(NodeV(r.bottom, IN, MIDDLE));
                    node_v.insert(NodeV(r.top, OUT, MIDDLE));
                } else if(l == r.left) {
                    node_v.insert(NodeV(r.bottom, IN, LEFT));
                    node_v.insert(NodeV(r.top, OUT, LEFT));
                } else {
                    node_v.insert(NodeV(r.bottom, IN, RIGHT));
                    node_v.insert(NodeV(r.top, OUT, RIGHT));
                }
            }
            int left, right, middle;
            in = pre_y = left = right = middle = 0;
            for(const auto& n: node_v) {
                if(!in) pre_y = n.y;
                if(!middle) if(left == 0 || right == 0) circumference += n.y - pre_y;
                if(n.in) {
                    ++in;
                    if(n.pos == MIDDLE) ++middle;
                    if(n.pos == LEFT) ++left;
                    if(n.pos == RIGHT) ++right;
                } else {
                    --in;
                    if(n.pos == MIDDLE) --middle;
                    if(n.pos == LEFT) --left;
                    if(n.pos == RIGHT) --right;
                }
                pre_y = n.y;
            }
        }

        cout << area << endl;
        if(R == 2) cout << circumference << endl;
    }
}

