		rep(i, h)for (int j = 1; j < w; j++)sum[i][j] += sum[i][j - 1];
		rep(i, w)for (int j = 1; j < h; j++)sum[j][i] += sum[j - 1][i];
		int a = 0, b = 0;
		rep(i, h)rep(j, w) {
			if (sum[i][j]) {
				a++;
				rep(k, 4) {
					int nx = i + dx[k], ny = j + dy[k];
					if (nx < 0 || nx >= h || ny < 0 || ny >= w || !sum[nx][ny])b++;
				}
			}
		}
		printf("%d\n", a);
		if (r == 2)printf("%d\n", b);
	}
}

