	vy.erase(unique(vy.begin(),vy.end()),vy.end());
	for(int i=0;i<n;i++){
		x1[i]=find(vx.begin(),vx.end(),x1[i])-vx.begin();
		x2[i]=find(vx.begin(),vx.end(),x2[i])-vx.begin();
		y1[i]=find(vy.begin(),vy.end(),y1[i])-vy.begin();
		y2[i]=find(vy.begin(),vy.end(),y2[i])-vy.begin();
	}
}

