			fie[x1[i]][y1[i]]++;
			fie[x1[i]][y2[i]]--;
			fie[x2[i]][y1[i]]--;
			fie[x2[i]][y2[i]]++;
		}
		for(int i=0;i<=vy.size();i++){
			for(int j=1;j<=vx.size();j++){
				fie[j][i]+=fie[j-1][i];
			}
		}
		for(int i=0;i<=vx.size();i++){
			for(int j=1;j<=vy.size();j++){
				fie[j][i]+=fie[j][i-1];
			}
		}
		long long res=0;
		for(int i=0;i<vx.size();i++){
			for(int j=0;j<vy.size();j++){
				if(fie[j][i]>0)res+=(long long)(vx[i+1]-vx[i])*(vy[j+1]-vy[j]);
