struct Rect{
  int x1,y1,x2,y2;
  Rect(){}
  Rect(int x1,int y1,int x2,int y2) : x1(x1),y1(y1),x2(x2),y2(y2) {}
  bool operator < (const Rect& oth)const{
    if(y2 == oth.y2){
      return y1 < oth.y1;
    }
    return y2 < oth.y2;
  }
};
struct Act{
  int up,down;
  Act(){}
  Act(int up,int down) : up(up),down(down) {}
};
int N,R;
Rect S[10002];
vector<Act> A[10002];
int main(){
  while(1){
    scanf("%d %d",&N,&R);
    if(!N && !R) break;
    int minx=100000,maxx=0;
    for(int i=0;i<N;i++){
      scanf("%d %d %d %d",&S[i].x1,&S[i].y1,&S[i].x2,&S[i].y2);
      minx = min(S[i].x1,minx); maxx = max(S[i].x2,maxx);
    }
    sort(S,S+N);
    for(int i=N-1;i>-1;i--){
      //      printf("%d %d %d %d\n",S[i].x1,S[i].y1,S[i].x2,S[i].y2);
      for(int x = S[i].x1; x < S[i].x2; x++){
	if(A[x].empty()){
	  A[x].push_back(Act(S[i].y2,S[i].y1));
	} else {
	  int dw = A[x].back().down;
	  if(dw-1 > S[i].y2){
	    A[x].push_back(Act(S[i].y2,S[i].y1));
	  } else if ( dw > S[i].y1 )
	    A[x].back().down = S[i].y1;
	}
      }
    }
    int res1=0;
    int res2=0;
    int sump=0;
    for(int i=0;i<(int)A[minx].size();i++){
      res1 += (A[minx][i].up - A[minx][i].down);
      res2 += (A[minx][i].up - A[minx][i].down);
    }
    sump = res2;
    res2 += (int)A[minx].size() * 2;
    for(int i=minx+1; i<=maxx; i++){
      int l=0;
      int sumk=0;
      int sumn=0;
      //      printf("%d %d\n",res1,res2);
      for(int j=0;j<(int)A[i].size();j++){
	res1 += (A[i][j].up - A[i][j].down);
	sumn += (A[i][j].up - A[i][j].down);
	bool f=false;
	do{
	  f=false;
	  if(A[i-1][l].up == A[i][j].up){
	    if(A[i-1][l].down >= A[i][j].down){
	      sumk += (A[i-1][l].up - A[i-1][l].down);
	      f=true;
	    }else{
	      sumk += (A[i][j].up - A[i][j].down);
	    }
	  } else if(A[i-1][l].up >= A[i][j].up && A[i][j].up >= A[i-1][l].down){
	    if(A[i-1][l].up >= A[i][j].down && A[i][j].down >= A[i-1][l].down){
	      sumk += (A[i][j].up - A[i][j].down);
	    } else{
	      sumk += (A[i-1][l].up - A[i][j].down);
	      f=true;
	    }
	  } else if(A[i][j].up >= A[i-1][l].up && A[i-1][l].up >= A[i][j].down){
	    if(A[i][j].up >= A[i-1][l].down && A[i-1][l].down >= A[i][j].down){
	      sumk += (A[i-1][l].up - A[i-1][l].down);
	      f=true;
	    } else {
	      sumk += (A[i][j].up - A[i-1][l].down);
	    }
	  }
	  if(f) l++;
	} while(f && l < (int)A[i-1].size());
      }
      res2 += (sump - sumk);
      res2 += (sumn - sumk);
      res2 += (int)A[i].size()*2;
      sump = sumn;
    }
    printf("%d\n",res1);
    if(R==2) printf("%d\n",res2);

    for(int i=minx; i<=maxx; i++) A[i].clear();
  }
}

