    for(int i = 1;i <= mx ; i++){
      for(int j = 1;j <= my + 1; j++){
	if(points.back().first == i && points.back().second == j){
	  tmp[j] += nums[points.back()];
	  pair<int, int> tmpr = points.back();
	  while(points.back() == tmpr)points.pop_back();
	}
	sheets[i % 2][j] = sheets[i % 2][j - 1] + tmp[j];
	if(sheets[i % 2][j] > 0)menseki++;
	if(r == 2){
	  if(sheets[i % 2][j] > 0 ){
	    if(sheets[(i - 1) % 2][j] == 0)gaishuu++;
	    if(sheets[i % 2][j - 1] == 0)gaishuu++;
	  }
	  if(sheets[i % 2][j] == 0 ){
	    if(sheets[(i - 1) % 2][j] > 0)gaishuu++;
	    if(sheets[i % 3][j - 1] > 0)gaishuu++;
	  }
	}
      }
    }
    printf("%d\n", menseki);
    if(r == 2){
      printf("%d\n", gaishuu);
    }
  }
}

