using namespace std;
typedef pair<int,int>P;
typedef list<P>L;
typedef L::iterator I;
int unite(L&l,I i,I j){
	int r=i->second>=j->first;
	if(r){
		i->second=max(i->second,j->second);
		l.erase(j);
	}
	return r;
}
void add(L&l,P p){
	I i=lower_bound(l.begin(),l.end(),p);
	i=l.insert(i,p);
	I j;
	for(;j=i,++j!=l.end()&&unite(l,i,j););
	for(;i!=l.begin()&&(j=i,unite(l,--i,j)););
}
int main(){
	int n,r;
	for(;cin>>n>>r,r;){
		L v[10000],h[10000];
		for(;n--;){
			int x1,y1,x2,y2,x,y;
			cin>>x1>>y1>>x2>>y2;
			for(x=x1;x<x2;x++){
				add(v[x],P(y1,y2));
			}
			for(y=y1;y<y2;y++){
				add(h[y],P(x1,x2));
			}
		}
		int s=0,c=0;
		for(int y=0;y<10000;y++){
			for(I i=h[y].begin();i!=h[y].end();++i)
				s+=i->second-i->first;
			c+=h[y].size()+v[y].size();
		}
		cout<<s<<endl;
		if(r==2){
			cout<<c*2<<endl;
		}
	}
	cout<<endl;
}

