using namespace std;

short node[10001];
short state_before[10001];
short map_before[10001];


map<int,list<pair<int,int>>> points;



void read_points(int tate){
    memcpy(map_before,node,sizeof node);
    memset(node,0,sizeof(node));    
    for(auto tmp:points[tate]){
        node[tmp.first]+=tmp.second;
    }
}
int main(){
    int n,r;
    int S=0;
    int L=0;
    ios::sync_with_stdio(false);

    while(1){
        cin>>n>>r;
        if(n==0&&r==0)break;

        points.clear();
        int x1,y1,x2,y2;
        int max_x=0,min_x=10000,max_y=0,min_y=10000;
        for(int i=0;i<n;i++){
            cin>>x1>>y1>>x2>>y2;

            if(points.find(y1)==points.end()){
                list<pair<int,int> > tmp;
                points[y1]=tmp;
            }
            if(points.find(y2)==points.end()){
                list<pair<int,int> > tmp;
                points[y2]=tmp;
            }
            points[y1].push_back(make_pair(x1,1));
            points[y1].push_back(make_pair(x2,-1));
            points[y2].push_back(make_pair(x1,-1));
            points[y2].push_back(make_pair(x2,1));
            if(max_x<x2)max_x=x2;
            if(max_y<y2)max_y=y2;
            if(min_x>x1)min_x=x1;
            if(min_y>y1)min_y=y1;

        }


        S=0;
        L=0;
        for(int h=min_y;h<=max_y;h++){

            read_points(h);
            int yoko_state=0;
            for(int w=min_x;w<=max_x;w++){

                yoko_state+=node[w];
                node[w]=yoko_state;
                if(h!=0){
                    node[w]+=state_before[w];
                }
                state_before[w]=node[w];
                if(node[w]>0){
                   S++;
                   if(w==0)L+=2;
                   else if(node[w-1]==0)L+=2;
                   if(h==0)L+=2;
                   else if(map_before[w]==0)L+=2;
                }


            }

        }


        cout<<S<<endl;
        if(r==2){
            cout<<L<<endl;
        }



    }



}

