using namespace std;

typedef pair<int,int> P;

bool cmp_x( const pair<P,P> &p , const pair<P,P> &q ){
  return p.F < q.F;
}

bool cmp_y( const pair<P,P> &p , const pair<P,P> &q ){
  return p.S < q.S;
}

vector<pair<P,P> >in;
vector<P>vx[10001],vy[10001];

int main(void){

  int n,r,x1,x2,y1,y2;

  while(cin >> n >> r){
    if(n==0 && r==0)break;

    for(int i=0;i<10001;i++){
      vx[i].clear();
      vy[i].clear();
    }

    in.clear();

    for(int i=0;i<n;i++){
      scanf("%d %d %d %d",&x1,&y1,&x2,&y2);
      in.push_back(make_pair(make_pair(x1,x2), make_pair(y1,y2)));
    }

    sort(in.begin(),in.end(),cmp_y);

    for(int i=0;i<in.size();i++)
      for(int j=in[i].F.F;j<in[i].F.S;j++)
	vx[j].push_back(make_pair(in[i].S.F,in[i].S.S));


      if(r==2){
	sort(in.begin(),in.end(),cmp_x);

	for(int i=0;i<in.size();i++)
	  for(int j=in[i].S.F;j<in[i].S.S;j++)
	    vy[j].push_back(make_pair(in[i].F.F,in[i].F.S));
      }

    int S=0,L=0;

    for(int i=0;i<10001;i++){
      if(vx[i].size()==0)continue;
      int b=vx[i][0].first,e=vx[i][0].second;

      for(int j=1;j<vx[i].size();j++){
	int B=vx[i][j].first,E=vx[i][j].second;

	if(e>=B)e=max(e,E);
	else S+=e-b,b=B,e=E,L+=2;
      }
      S+=e-b,L+=2;
    }

    cout << S << endl;


    if(r==2){

      for(int i=0;i<10001;i++){
	if(vy[i].size()==0)continue;
	int b=vy[i][0].first,e=vy[i][0].second;

	for(int j=1;j<vy[i].size();j++){
	  int B=vy[i][j].first,E=vy[i][j].second;

	  if(e>=B)e=max(e,E);
	  else b=B,e=E,L+=2;
	}
	L+=2;
      }
      cout << L << endl;
    }
  }
  return 0;
}

