int mm[SIZE][SIZE];
int mo[5] = {0,1,0,-1,0};
void calc(int n,int r){
    int x_1,x_2,y_1,y_2;
    int ansV,ansL;


    for(int i=0;i<SIZE;i++){
        for(int j=0;j<SIZE;j++){
            mm[i][j]=0;
        }
    }

    for(int i=0;i<n;i++){
        scanf("%d%d%d%d",&x_1,&y_1,&x_2,&y_2);

        x_1++;
        y_1++;
        x_2++;
        y_2++;

        mm[x_1][y_1]++;
        mm[x_2][y_1]--;
        mm[x_1][y_2]--;
        mm[x_2][y_2]++;
    }

    for(int i=1;i<SIZE;i++){
        for(int j=2;j<SIZE;j++){
            mm[j][i]+=mm[j-1][i];
        }
    }

    for(int i=1;i<SIZE;i++){
        for(int j=2;j<SIZE;j++){
            mm[i][j]+=mm[i][j-1];
        }
    }

    for(int i=1;i<SIZE;i++){
        for(int j=1;j<SIZE;j++){
            if(mm[i][j]>0){
                ansV++;

                for(int k=0;k<4;k++){
                    if(mm[i+mo[k]][j+mo[k+1]]==0){
                        ansL++;
                    }
                }

            }
        }
    }

    printf("%d\n",ansV);

    if(r==2)
        printf("%d\n",ansL);

    return;
}

