							if(nx==0 || nx==vx.size())res2+=vy[j+1]-vy[j],cnt+=vy[j+1]-vy[j];
							else if(fie[nx][j]==0)res2+=vy[j+1]-vy[j],cnt+=vy[j+1]-vy[j];
