#include <stdio.h>

int n;
short min_x1, max_x2, min_y1, max_y2;
short x1[10000], x2[10000], y1[10000], y2[10000];
short tiles[10002][10002];

void pre_process() {
    short i;

    for (i=0;i<n;i++) {
        tiles[y1[i]][x1[i]]++;
        tiles[y1[i]][x2[i]]--;
        tiles[y2[i]][x2[i]]++;
        tiles[y2[i]][x1[i]]--;
    }

    for (i=min_y1;i<max_y2+1;i++) {
        int j;
        for (j=min_x1+1;j<max_x2+1;j++) {
            tiles[i][j] += tiles[i][j-1];
        }
    }


    for (i=min_y1+1;i<max_y2+1;i++) {
        int j;
        for (j=min_x1;j<max_x2+1;j++) {
            tiles[i][j] += tiles[i-1][j];
        }
    }

    return;
}

int surface() {
    short i;
    int sum = 0;

    for (i=min_y1;i<max_y2;i++) {
        int j;
        for (j=min_x1;j<max_x2;j++) {
            if (tiles[i][j] > 0) {
                sum++;
            }
        }
    }

    return sum;
}

int perimeter() {
    short i;
    int sum = 0;

    for (i=min_y1;i<max_y2+2;i++) {
        short j;
        for (j=min_x1;j<max_x2+2;j++) {
            short add = tiles[i][j] + tiles[i-1][j];

            if (add > 0 && (tiles[i][j] == 0 || tiles[i-1][j] == 0)) {
                sum++;
            }

            add = tiles[i][j] + tiles[i][j-1];

            if (add > 0 && (tiles[i][j] == 0 || tiles[i][j-1] == 0)) {
                sum++;
            }
        }
    }

    return sum;
}

int main() {
    while (1) {
        short i;
        int r;

        scanf("%d %d", &n, &r);
        if (n == 0 && r ==0) return 0;

        min_x1 = 10000;
        min_y1 = 10000;
        max_x2 = 0;
        max_y2 = 0;
        for (i=0;i<n;i++) {
            scanf("%hd %hd %hd %hd", &x1[i], &y1[i], &x2[i], &y2[i]);
            x1[i]++;
            x2[i]++;
            y1[i]++;
            y2[i]++;
            if (min_x1 > x1[i]) min_x1 = x1[i];
            if (max_x2 < x2[i]) max_x2 = x2[i];
            if (min_y1 > y1[i]) min_y1 = y1[i];
            if (max_y2 < y2[i]) max_y2 = y2[i];
        }

        for (i=0;i<10002;i++) {
            short j;
            for (j=0;j<10002;j++) {
                tiles[i][j] = 0;
            }
        }
        pre_process();

        printf("%d\n", surface());

        if (r == 2) {
            printf("%d\n", perimeter());
        }        
    }
}

