			scanf("%d%d%d%d", &x1, &y1, &x2, &y2);

			x1 += 1, y1 += 1, x2 += 1, y2 += 1;

			sheet[x1].push_back(make_pair(y1, 1));
			sheet[x1].push_back(make_pair(y2, -1));
			sheet[x2].push_back(make_pair(y1, -1));
			sheet[x2].push_back(make_pair(y2, 1));
		}

		int size = 0, round = 0;

		int dp[2][maxsize] = {0};
		int *imos1, *imos2;
		imos1 = dp[0];
		imos2 = dp[1];

		for(int x = 1; x < maxsize; ++x) {

			fill(imos2, imos2 + maxsize, 0);

			for(int i = 0; i < sheet[x].size(); ++i) {
				int pos = sheet[x][i].first, val = sheet[x][i].second;
				imos2[pos] += val;
			}

			for(int y = 1; y < maxsize; ++y)
				imos2[y] = imos2[y - 1] + imos2[y];
			for(int y = 1; y < maxsize; ++y)
				imos2[y] = imos2[y] + imos1[y];

			for(int y = 1; y < maxsize; ++y) {
				int up = (imos1[y] == 0 && imos2[y] > 0) || (imos1[y] > 0 && imos2[y] == 0);
				int left = (imos2[y - 1] == 0 && imos2[y] > 0 || imos2[y - 1] > 0 && imos2[y] == 0);
				int exist = imos2[y] > 0;
				size += exist;
				round += up + left;
			}

			swap(imos1, imos2);
		}

		printf("%d\n", size);
		if(r == 2)
			printf("%d\n", round);
	}
	return 0;
}

