using namespace std;

struct Square {int left, top, right, bottom;};

int main() {
    int n, r;
    while(cin >> n >> r, n | r) {
        vector<int> x = {0}, y = {0};
        vector<Square> sheet(n);
        for(auto& s: sheet) {
            cin >> s.left >> s.top >> s.right >> s.bottom;
            y.push_back(++s.top);
            y.push_back(++s.bottom);
            x.push_back(++s.left);
            x.push_back(++s.right);
        }
        sort(y.begin(), y.end());
        sort(x.begin(), x.end());
        y.erase(unique(y.begin(), y.end()), y.end());
        x.erase(unique(x.begin(), x.end()), x.end());

        vector<vector<int>> dp(y.size(), vector<int>(x.size(), 0));
        for(const auto& s: sheet) {
            int top = lower_bound(y.begin(), y.end(), s.top) - y.begin();
            int bottom = lower_bound(y.begin(), y.end(), s.bottom) - y.begin();
            int left = lower_bound(x.begin(), x.end(), s.left) - x.begin();
            int right = lower_bound(x.begin(), x.end(), s.right) - x.begin();
            ++dp[top][left];
            --dp[top][right];
            --dp[bottom][left];
            ++dp[bottom][right];
        }

        for(int i = 0; i < y.size(); ++i) for(int j = 1; j < x.size(); ++j) dp[i][j] += dp[i][j - 1];
        for(int j = 0; j < x.size(); ++j) for(int i = 1; i < y.size(); ++i) dp[i][j] += dp[i - 1][j];

        int area = 0, circumference = 0;
        for(int i = 1; i < y.size() - 1; ++i) for(int j = 1; j < x.size() - 1; ++j) {
            if(!dp[i][j]) continue;
            area += (y[i + 1] - y[i]) * (x[j + 1] - x[j]);
            if(!dp[i - 1][j]) circumference += (x[j + 1] - x[j]);
            if(!dp[i + 1][j]) circumference += (x[j + 1] - x[j]);
            if(!dp[i][j - 1]) circumference += (y[i + 1] - y[i]);
            if(!dp[i][j + 1]) circumference += (y[i + 1] - y[i]);
        }
        cout << area << endl;
        if(r == 2) cout << circumference << endl;
    }
}

