const int dx[]={1,0,-1,0},dy[]={0,1,0,-1};

int min_x,min_y,max_x,max_y;

inline void Imos(vector<vector<unsigned char> > &plane){
	REP(y,0,plane[0].size()){
		REP(x,1,plane.size()){
			plane[x][y]+=plane[x-1][y];
		}
	}
	REP(x,0,plane.size()){
		for(int y=plane[0].size()-2; y>=0; --y){
			plane[x][y]+=plane[x][y+1];
		}
	}
}

inline int CalcSize(vector<vector<unsigned char> > &plane){
	int result=0;
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(plane[x][y]>0) ++result;
		}
	}
	return result;
}

inline int BFS(vector<vector<unsigned char> > &plane,vector<vector<bool> > &has_done,int x,int y,int W,int H){
	int result=0;
	queue<P> que;
	que.push(MP(x,y));
	has_done[x][y]=true;
	while(!que.empty()){
		P p=que.front();
		que.pop();
		REP(i,0,4){
			int nx=p.F_+dx[i],ny=p.S_+dy[i];	
			if(0<=nx&&nx<W&&0<=ny&&ny<H&&!has_done[nx][ny]){
				if(plane[nx][ny]>0){
					que.push(MP(nx,ny));
					has_done[nx][ny]=true;
				}else ++result;
			}
		}
	}
	return result;
}

inline int CalcLength(vector<vector<unsigned char> > &plane,int n,vector<int> &X,vector<int> &Y){
	vector<vector<bool> > has_done(plane.size(),vector<bool>(plane[0].size()));
	int result=0;
	/*
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(!has_done[x][y]&&plane[x][y]>0){
				result+=BFS(plane,has_done,x,y,plane.size(),plane[0].size());
			}
		}
	}
	*/
	REP(i,0,n){
		if(!has_done[X[i]][Y[i]]&&plane[X[i]][Y[i]]>0) result+=BFS(plane,has_done,X[i],Y[i],plane.size(),plane[0].size());
	}
	return result;
}

int main(){
	std::ios::sync_with_stdio(false);
	std::cin.tie(0);
	int N,R;
	while(cin >> N >> R&&N&&R){
		min_x=min_y=INF;
		max_x=max_y=0;
		vector<int> X[2],Y[2];
		REP(i,0,N){
			REP(j,0,2){
				int x,y;
				cin >> x >> y;
				if(j==0){
					++x;
					++y;
				}
				X[j].push_back(x);
				Y[j].push_back(y);
				min_x=min(min_x,x);
				min_y=min(min_y,y);
				max_x=max(max_x,x);
				max_y=max(max_y,y);
			}
		}
		vector<vector<unsigned char> > plane(max_x+4-min_x,vector<unsigned char>(max_y+4-min_y));
		REP(i,0,N){
			plane[X[0][i]+1-min_x][Y[1][i]+1-min_y]+=1;
			plane[X[1][i]+2-min_x][Y[0][i]-min_y]+=1;
			plane[X[0][i]+1-min_x][Y[0][i]-min_y]+=-1;
			plane[X[1][i]+2-min_x][Y[1][i]+1-min_y]+=-1;
		}
		Imos(plane);
		cout << CalcSize(plane) << endl;
		if(R==2) cout << CalcLength(plane,N,X[0],Y[0]) << endl;
	}
	return 0;
}

