using namespace std;
typedef vector<int> PP;
typedef vector<PP> P;
int n,r;
int x1[10001],x2[10001],y1[10001],y2[10001];
int fie[3000][3000];
vector<int> vx;
vector<int> vy;

void zatu(){
	for(int i=0;i<n;i++){
		for(int j=-1;j<=1;j++){
			vx.push_back(x1[i]+j);
			vx.push_back(x2[i]+j);
			vy.push_back(y1[i]+j);
			vy.push_back(y2[i]+j);
		}
	}
	vx.push_back(0);
	vx.push_back(10000);
	vy.push_back(0);
	vy.push_back(10000);
	sort(vx.begin(),vx.end());
	sort(vy.begin(),vy.end());
	vx.erase(unique(vx.begin(),vx.end()),vx.end());
	vy.erase(unique(vy.begin(),vy.end()),vy.end());
	for(int i=0;i<n;i++){
		x1[i]=find(vx.begin(),vx.end(),x1[i])-vx.begin();
		x2[i]=find(vx.begin(),vx.end(),x2[i])-vx.begin();
		y1[i]=find(vy.begin(),vy.end(),y1[i])-vy.begin();
		y2[i]=find(vy.begin(),vy.end(),y2[i])-vy.begin();
	}
}

int main(void){
	while(1){
		scanf("%d%d",&n,&r);
		if(n==0 && r==0)break;
		vx.clear();
		vy.clear();
		memset(fie,0,sizeof(fie));
		for(int i=0;i<n;i++)scanf("%d%d%d%d",&x1[i],&y1[i],&x2[i],&y2[i]);
		zatu();
		for(int i=0;i<n;i++){
			fie[x1[i]][y1[i]]++;
			fie[x1[i]][y2[i]]--;
			fie[x2[i]][y1[i]]--;
			fie[x2[i]][y2[i]]++;
		}
		for(int i=0;i<=vy.size();i++){
			for(int j=1;j<=vx.size();j++){
				fie[j][i]+=fie[j-1][i];
			}
		}
		for(int i=0;i<=vx.size();i++){
			for(int j=1;j<=vy.size();j++){
				fie[j][i]+=fie[j][i-1];
			}
		}
		long long res=0;
		for(int i=0;i<vx.size();i++){
			for(int j=0;j<vy.size();j++){
				if(fie[j][i]>0)res+=(long long)(vx[i+1]-vx[i])*(vy[j+1]-vy[j]);
			}
		}

		cout << res << endl;
		if(r==2)cout << res << endl;
	}
    return 0;
}

#include <iostream>
#include <algorithm>
#include <map>
#include <cstring>
#define FMAX 10000
#define initarr(a) memset(a, 0, sizeof(a))
using namespace std;
typedef map<short, map<short, short> > V;

short calc_x_arr[FMAX];

void add_vertex(V &vertexes, short x, short y, short t) {
  auto &xv = vertexes[y];
  auto &sum = xv[x];
  sum += t;
}

int main() {
  short n, r;
  while (cin >> n >> r, n || r) {
    // load
    short x1, y1, x2, y2;
    short min_x = FMAX, min_y = FMAX, max_x = 0, max_y = 0;
    V vertexes;
    for (short i = 0; i < n; i++) {
      cin >> x1 >> y1 >> x2 >> y2;
      add_vertex(vertexes, x1, y1, 1);
      add_vertex(vertexes, x1, y2, -1);
      add_vertex(vertexes, x2, y1, -1);
      add_vertex(vertexes, x2, y2, 1);
      min_x = min(min_x, x1);
      min_y = min(min_y, y1);
      max_x = max(max_x, x2);
      max_y = max(max_y, y2);
    }

    // calc
    int area = 0, perimeter = 0;
    short prev_y = -1;
    short prev_partial_vertical_perimeter = 0;
    short prev_partial_area = 0;
    initarr(calc_x_arr);
    for (auto yvit = vertexes.begin(); yvit != vertexes.end(); yvit++) {
      area += prev_partial_area * (yvit->first - prev_y - 1);
      prev_partial_area = 0;
      if (r == 2) {
        perimeter += prev_partial_vertical_perimeter * (yvit->first - prev_y - 1);
        prev_partial_vertical_perimeter = 0;
      }
      short left = 0;
      short xsum = 0;
      auto xvit = yvit->second.begin();
      for (short x = min_x; x <= max_x; x++) {
        if (x == xvit->first) {
          xsum += xvit->second;
          if (++xvit == yvit->second.end()) {
            xvit = yvit->second.begin();
          }
        }
        short current = xsum + calc_x_arr[x];
        if (current != 0) prev_partial_area++;
        if (r == 2) {
          bool vdelta = left == 0 ^ current == 0;
          prev_partial_vertical_perimeter += vdelta;
          perimeter += vdelta + (calc_x_arr[x] == 0 ^ current == 0);
        }
        left = calc_x_arr[x] = current;
      }
      area += prev_partial_area;
      prev_y = yvit->first;
    }

    cout << area << endl;
    if (r == 2) cout << perimeter << endl;
  }
  return 0;
}

