#include<cstdio>
#include<algorithm>
#include<queue>
#include<map>
#include<cstring>
using namespace std;
typedef pair<int, int> P;
typedef pair<P, int> T;
#define INF (1 << 30)

priority_queue<T, vector<T>, greater<T> > pq;
int ax, ay, bx, by, n, r, p;
int round, area, left_side, right_side, bottom, top;
int imos[3][10500];
int main(){
    while(scanf("%d%d", &n, &r), r){
	memset(imos, 0, sizeof(imos));
	round = area = 0;
	p = 0;
	left_side = bottom = 10500;
	right_side = top = 1;
	for(int i = 0;i < n;i++){
	    scanf("%d%d%d%d", &ax, &ay, &bx, &by);
	    left_side = min(left_side, ++ax);
	    right_side = max(right_side, ++bx);
	    bottom = min(bottom, ++ay);
	    top = max(top, ++by);	    
	    pq.push(T(P(ax, ay), 1));
	    pq.push(T(P(ax, by), -1));
	    pq.push(T(P(bx, ay), -1));
	    pq.push(T(P(bx, by), 1));
	}
	pq.push(T(P(INF, INF), 0));
	for(int i = left_side;i < right_side + 2;i++){
	    for(int j = bottom;j < top + 2;j++){
		imos[i % 3][j] = imos[i % 3][j - 1];
		while(pq.top().first == P(i, j)){
		    imos[i % 3][j] += pq.top().second;pq.pop();
		}
	    }
	    for(int j = bottom;j < top + 2;j++){
		imos[i % 3][j] += imos[(i - 1) % 3][j];
		if(imos[i % 3][j])area++;
		if(!!imos[i % 3][j] != !!imos[i % 3][j - 1])round++;
		if(!!imos[i % 3][j] != !!imos[(i - 1) % 3][j])round++; 
	    }

	}
	pq.pop();
	printf("%d\n", area);
	if(r == 2)printf("%d\n", round);
    }
    return 0;
}

