#define mp         make_pair
#define pb         push_back

const int N = 10010;

class st{
public:
  int height;
  char out;//1 or -1
  bool operator<(const st & a)const{
    if (height != a.height)return height < a.height;
    return out > a.out;
  }
};



int x[N];
vector<st> inx[N];

void makedata(int n,int *x1,int *x2,int *y1,int *y2,
	      int &px,int *xd,vector<st> *IN){
  rep(i,n){
    xd[px++]=x1[i];
    xd[px++]=x2[i];
  }
  sort(xd,xd+px);
  px=unique(xd,xd+px)-xd;
  rep(i,px)IN[i].clear();
  //cout <<"allx ";rep(i,px)cout << x[i]<<" ";cout << endl;
  rep(i,n){
    int j=lower_bound(xd,xd+px,x1[i])-xd;
    while(j < px && xd[j] < x2[i]){
      IN[j].pb((st){y1[i],1});
      IN[j].pb((st){y2[i],-1});
      j++;
    }
  }
}

pair<int,int> solve(int n,vector<st> *in,int *c){
  int area=0;
  int len=0;
  rep(i,n-1){
    if (in[i].size() == 0)continue;
    sort(in[i].begin(),in[i].end());
    int cnt=0,prev=-100;
    int width;
    width=c[i+1]-c[i];
    rep(j,in[i].size()){
      if (cnt > 0){
	area+=(in[i][j].height-prev)*width;
      }
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      cnt+=in[i][j].out;
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      prev=in[i][j].height;
    }
  }
  return mp(area,len);
}

main(){
  int n,q;
  static int x1[N],y1[N],x2[N],y2[N];
  while(cin>>n>>q && n){
    rep(i,n){
      cin>>x1[i]>>y1[i]>>x2[i]>>y2[i];
    }
    int px=0,py=0;
    makedata(n,x1,x2,y1,y2,px,x,inx);
    pair<int,int> ansx=solve(px,inx,x);

    cout << ansx.first << endl;
    if (q == 2){
      px=0;
      makedata(n,y1,y2,x1,x2,px,x,inx);
      pair<int,int> ansy= solve(px,inx,x);      
      cout << ansx.second+ansy.second << endl;
    }
  }
  return false;
}

#include <iostream>
#include <unordered_map>
#include <vector>

#define loop(n, i) for(int i=0;i<n;i++)
#define repeat(n) for(int __i=0;__i<n;__i++)
#define loop_from_to(from, to, i) for(int i=from;i<=to;i++)
#define multi_cin(n, array) loop(n,i){cin>>array[i];}
#define assign_if_smaller(var, val) if(var>val)var=val;
#define assign_if_larger(var, val) if(var<val)var=val;

using namespace std;
int x_size, y_size;
int x_min, x_max;
int y_min, y_max;
int type;
unordered_map<int, vector<tuple<int, int, int>>> sheets;

void init() {
  x_min = 0; y_min = 0; x_max = 0; y_max = 0;
  x_size = 0; y_size = 0;
}

void add_sheet(int x1, int y1, int x2, int y2) {
  assign_if_smaller(x_min, x1);
  assign_if_smaller(y_min, y1);
  assign_if_larger(x_max, x2);
  assign_if_larger(y_max, y2);
  x_size = x_max + 1;
  y_size = y_max + 1;

  auto t = make_tuple(y1, x2, y2);
  if (sheets.count(x1) == 0) {
    sheets[x1] = {t};
  } else {
    sheets[x1].push_back(t);
  }
}
vector<tuple<int, int, int>>& find_end_points(int x, int y) {
  return sheets[x];
}

void calc_and_output() {
  long area = 0;
  long lines = 0;

  int right_area_end_point[y_size];
  loop(y_size, i) right_area_end_point[i] = -1;

  bool* is_area[2];
  is_area[0] = new bool[y_size];
  is_area[1] = new bool[y_size];
  loop(y_size, i) {
    is_area[0][i] = false;
    is_area[1][i] = false;
  }

  loop(x_size, x_i) {
    // update right_area_end_point
    auto& points = find_end_points(x_i, 0);

    // per point operation
    for (auto&& point : points) {
      int y_i = get<0>(point), x = get<1>(point), y = get<2>(point);

      loop_from_to(y_i, y - 1, p_i) {
        assign_if_larger(right_area_end_point[p_i], x);
      }
    }

    loop(y_size, y_i) {

      // included in some sheets
      if (right_area_end_point[y_i] > x_i) {
        area += 1;
        is_area[x_i % 2][y_i] = true;
      } else {
        is_area[x_i % 2][y_i] = false;
      }

      // line check
      if (type == 2) {
        // left line exists
        if (is_area[(x_i % 2) ^ 1][y_i] != is_area[x_i % 2][y_i]) lines += 1;

        // bottom line exists
        if (
            (y_i == 0 && is_area[x_i % 2][y_i]) ||
            (y_i > 0 && (is_area[x_i % 2][y_i - 1] != is_area[x_i % 2][y_i]))
            )
          lines += 1;
      }
    }

  }

  cout << area << endl;
  if (type == 2) cout << lines << endl;

  delete is_area[0];
  delete is_area[1];
}

int main(int argc, char const* argv[])
{
  while (true) {
    int n; cin >> n >> type;
    if (n == 0 && type == 0) break;

    init();

    repeat(n) {
      int p[4];
      multi_cin(4, p);
      add_sheet(p[0], p[1], p[2], p[3]);
    }

    calc_and_output();
    sheets.clear();
  }
  return 0;
}

