int n,r;

struct block{
	int x1,x2,y,f;
};

block b[20001];
int fie[10000][2];

bool comp(const block &b1,const block &b2){
	return b1.y<b2.y;
}

int main(void){
	while(1){
		scanf("%d%d",&n,&r);
		if(n==0 && r==0)break;
		memset(fie,0,sizeof(fie));
		for(int i=0;i<n;i++){
			int y1,y2;
			scanf("%d%d%d%d",&b[i*2].x1,&y1,&b[i*2].x2,&y2);
			b[i*2].x2--;
			b[i*2+1].x1=b[i*2].x1;
			b[i*2+1].x2=b[i*2].x2;
			b[i*2].y=y1;
			b[i*2+1].y=y2;
			b[i*2].f=1;
			b[i*2+1].f=-1;
		}
		sort(b,b+n*2,comp);
		int ny=0;
		int now=0,prev=1;
		int res=0,res2=0;
		for(int i=0;i<n*2;i++){
			if(ny!=b[i].y){
				swap(now,prev);
				int cnt=0;
				int cnt2=0,bt=0,cnt3=0;
				for(int j=0;j<=10000;j++){
					if(fie[j][now]>0 && fie[j][prev]==0)cnt3++;
					if(fie[j][now]==0 && fie[j][prev]>0)cnt3++;
					fie[j][now]=fie[j][prev];
					if(fie[j][now]>0){
						cnt++;
						if(bt==0)cnt2++,bt=1;
					}else bt=0;
				}
				res+=cnt*(b[i].y-ny);
				res2+=cnt2*2*(b[i].y-ny)+cnt3;
			}
			ny=b[i].y;
			for(int j=b[i].x1;j<=b[i].x2;j++)fie[j][now]+=b[i].f;
			//printf("%d\n",res2);

		}
		printf("%d\n",res);
		if(r==2){
			swap(now,prev);
			int cnt3=0;
			for(int j=0;j<=10000;j++){
				if(fie[j][now]>0 && fie[j][prev]==0)cnt3++;
				if(fie[j][now]==0 && fie[j][prev]>0)cnt3++;
				fie[j][now]=fie[j][prev];
			}
			res2+=cnt3;
			printf("%d\n",res2);
		}
	}
	return 0;
}

#include<bits/stdc++.h>
#define rep(i,n)for(int i=0;i<(n);i++)
using namespace std;

int sum[2][10001], c[2][10001];
vector<int>E[10001], G[10001];
int main() {
	int n, r;
	while (scanf("%d%d", &n, &r), n) {
		memset(sum, 0, sizeof(sum));
		memset(c, 0, sizeof(c));
		rep(i, 10001)E[i].clear(), G[i].clear();
		int h = 0, w = 0;
		rep(i, n) {
			int x1, y1, x2, y2; scanf("%d%d%d%d", &x1, &y1, &x2, &y2);
			h = max(h, x2 + 1); w = max(w, y2 + 1);
			E[x1].push_back(y1); E[x2].push_back(y2);
			G[x1].push_back(y2); G[x2].push_back(y1);
		}
		int a = 0, b = 0;
		rep(i, h) {
			rep(j, w)sum[i & 1][j] = c[i & 1][j] = c[!(i & 1)][j];
			for (int j : E[i])sum[i & 1][j]++, c[i & 1][j]++;
			for (int j : G[i])sum[i & 1][j]--, c[i & 1][j]--;
			for (int j = 1; j < w; j++)sum[i & 1][j] += sum[i & 1][j - 1];
			rep(j, w) {
				if (sum[i & 1][j])a++;
				if (!sum[i & 1][j] != !sum[!(i & 1)][j])b++;
				if (j == 0 && sum[i & 1][j])b++;
				if (j && !sum[i & 1][j] != !sum[i & 1][j - 1])b++;
			}
		}
		printf("%d\n", a);
		if (r == 2)printf("%d\n", b);
	}
}

