using std::cin;
using std::cout;
using std::endl;
using std::vector;
using std::for_each;
using std::count_if;
using std::accumulate;
using std::max_element;

using rect_t = struct _rect {int x1,y1,x2,y2;}; // type of rectangle

int main() {
	while (true) {
		int n; // number of rectangles
		int r; // type of problem
		cin >> n >> r;
		if (n == 0 && r == 0) {
			break;
		}
		vector<rect_t> rects(n); // n rectangles
		// read coordinates
		for_each(rects.begin(),rects.end(),[](rect_t &r){cin >> r.x1 >> r.y1 >> r.x2 >> r.y2;});
		// find max x2 and y2
		auto max_x2 = max_element(rects.begin(),rects.end(),[](rect_t r1,rect_t r2){return r1.x2 < r2.x2;});
		auto max_y2 = max_element(rects.begin(),rects.end(),[](rect_t r1,rect_t r2){return r1.y2 < r2.y2;});
		// create sheet
		vector< vector<bool> > sheet(max_x2->x2);
		for_each(sheet.begin(),sheet.end(),[max_y2](vector<bool> &row){row = vector<bool>(max_y2->y2);});
		// fill rectangles
		for (auto r : rects) {
			for (int i = r.x1;i != r.x2;++i) {
				for (int j = r.y1;j != r.y2;++j) {
					if (!sheet[i][j]) {
						sheet[i][j] = true;
					}
				}
			}
		}
		// count area
		int area = accumulate(sheet.begin(),sheet.end(),0,[](int a,vector<bool> r){
			return a + count_if(r.begin(),r.end(),[](bool b){return b;});
		});
		cout << area << endl;
		if (r == 2) {
			// count surrounding length
			int sur_len = 0;
			for (int i = 0;i != max_x2->x2;++i) {
				for (int j = 0;j != max_y2->y2;++j) {
					if (sheet[i][j]) {
						if (i - 1 < 0 || !sheet[i-1][j]) {
							++sur_len;
						}
						if (i + 1 == max_x2->x2 || !sheet[i+1][j]) {
							++sur_len;
						}
						if (j - 1 < 0 || !sheet[i][j-1]) {
							++sur_len;
						}
						if (j + 1 == max_y2->y2 || !sheet[i][j+1]) {
							++sur_len;
						}
					}
				}
			}
			cout << sur_len << endl;
		}
	}
}

