    short l=-1,h=-1;
    short width;
    width=c[i+1]-c[i];
    rep(j,in[i].size()){
      short &nowl=in[i][j].fir,&nowh=in[i][j].sec;
      if (nowl > h){
	len+=2*width;
	area+=(nowh-nowl)*width;
      }else if (nowl == h){
