	len+=width;
      }
      cnt+=in[i][j].out;
      //if (cnt == 0 && in[i][j].height != prev){
      if (cnt == 0){
	len+=width;
      }
      prev=in[i][j].height;
    }
  }
  return mp(area,len);
}

