int main(){
	while(1){
		int ans=0,ans2=0,minx=10001,miny=10001,maxx=1,maxy=1;
		scanf("%d%d",&n,&r);
		if(n==0 && r==0)break;
		memset(g,0,sizeof(g));
		for(int i=0;i<n;i++){
			int x1,y1,x2,y2;
			scanf("%d%d%d%d",&x1,&y1,&x2,&y2);
			x1++,y1++,x2++,y2++;
			minx=min(min(x1,x2),minx);
			miny=min(min(y1,y2),miny);
			maxx=max(max(x1,x2),maxx);
			maxy=max(max(y1,y2),maxy);
			g[x1][y1]-=1;
			g[x2][y1]+=1;
			g[x1][y2]+=1;
			g[x2][y2]-=1;
		}
		for(int i=miny;i<=maxy;i++){
			int t=0;
			for(int j=minx;j<=maxy;j++){
				t+=g[j][i];
				g[j][i]=t;
			}
		}
		for(int i=minx;i<=maxx;i++){
			int t=0;
			for(int j=maxy;j>=miny;j--){
				t+=g[i][j];
				g[i][j]=t;
				if(g[i][j]>0){
					ans++;
				}
			}
		}
		printf("%d\n",ans);
		if(r==2){
			for(int i=minx;i<=maxx;i++){
				for(int j=miny;j<=maxy;j++){
					if(g[i][j]>=1){
						if(g[i-1][j]<=0)ans2++;
						if(g[i+1][j]<=0)ans2++;
						if(g[i][j-1]<=0)ans2++;
						if(g[i][j+1]<=0)ans2++;
					}
				}
			}
			printf("%d\n",ans2);
		}
	}
    return 0;
}

