#define MP(a,b) make_pair((a),(b))
#define F_ first
#define S_ second

typedef long long int lli;
typedef pair<int,int> P;

const int INF=1<<30;
//const long long int INF_=1<<58;
const int dx[]={1,0,-1,0},dy[]={0,1,0,-1};

inline void Imos(vector<vector<short> > &plane){
	REP(y,0,plane[0].size()){
		REP(x,1,plane.size()){
			plane[x][y]+=plane[x-1][y];
		}
	}
	REP(x,0,plane.size()){
		for(int y=plane[0].size()-2; y>=0; --y){
			plane[x][y]+=plane[x][y+1];
		}
	}
}

inline int CalcSize(vector<vector<short> > &plane){
	int result=0;
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(plane[x][y]>0) ++result;
		}
	}
	return result;
}

inline int BFS(vector<vector<short> > &plane,vector<vector<bool> > &has_done,int x,int y,int W,int H){
	int result=0;
	queue<P> que;
	que.push(MP(x,y));
	has_done[x][y]=true;
	while(!que.empty()){
		P p=que.front();
		que.pop();
		REP(i,0,4){
			int nx=p.F_+dx[i],ny=p.S_+dy[i];	
			if(0<=nx&&nx<W&&0<=ny&&ny<H&&!has_done[nx][ny]){
				if(plane[nx][ny]>0){
					que.push(MP(nx,ny));
					has_done[nx][ny]=true;
				}else ++result;
			}
		}
		//cout << result << ' ' << p.F_ << ' ' << p.S_ << endl;
	}
	return result;
}

inline int CalcLength(vector<vector<short> > &plane){
	vector<vector<bool> > has_done(plane.size(),vector<bool>(plane[0].size()));
	int result=0;
	REP(x,0,plane.size()){
		REP(y,0,plane[0].size()){
			if(!has_done[x][y]&&plane[x][y]>0){
				result+=BFS(plane,has_done,x,y,plane.size(),plane[0].size());
			}
		}
	}
	return result;
}

int main(){
	std::ios::sync_with_stdio(false);
	std::cin.tie(0);
	int N,R;
	while(cin >> N >> R&&N&&R){
		int min_x,min_y,max_x,max_y;
		min_x=min_y=INF;
		max_x=max_y=0;
		vector<int> X[2],Y[2];
		REP(i,0,N){
			REP(j,0,2){
				int x,y;
				cin >> x >> y;
				if(j==0){
					++x;
					++y;
				}
				X[j].push_back(x);
				Y[j].push_back(y);
				min_x=min(min_x,x);
				min_y=min(min_y,y);
				max_x=max(max_x,x);
				max_y=max(max_y,y);
			}
		}
		vector<vector<short> > plane(max_x+4,vector<short>(max_y+4));
		REP(i,0,N){
			plane[X[0][i]+1][Y[1][i]+1]+=1;
			plane[X[1][i]+2][Y[0][i]]+=1;
			plane[X[0][i]+1][Y[0][i]]+=-1;
			plane[X[1][i]+2][Y[1][i]+1]+=-1;
		}
		Imos(plane);
		/*
		for(int y=plane[0].size()-1; y>=0; --y){
			REP(x,0,plane.size()){
				cout << plane[x][y] << ' ';
			}
			cout << endl;
		}
		*/
		cout << CalcSize(plane) << endl;
		if(R==2) cout << CalcLength(plane) << endl;
	}
	return 0;
}

