          s++;
        }
        f[i][j] = 1;
      }
    }
  } 
  printf("%d\n",s);
  if(r==2){
    l = 0;
    for(i=0;i<Mx-1;i++){
      l+=(f[i][0]+f[i][Mx-1]);
      for(j=0;j<My-1;j++){
        l+=(f[i][j]^f[i][j+1]);
      }
    }
    for(j=0;j<My-1;j++){
      l+=(f[0][j]+f[My-1][j]);
      for(i=0;<Mx-1;i++){
        l+=(f[i+1][j]^f[i][j]);
      }
    }
    printf("%d\n",l);
  }
