#include <stdio.h>
#include <stdbool.h>

#define MAX_RECT 10000

#define MIN(a,b) ((a)<(b)?(a):(b))
#define MAX(a,b) ((a)>(b)?(a):(b))

typedef struct {
	int x1, y1, x2, y2;
} rect_t;

rect_t rects[MAX_RECT];
int rect_num;

int check_collide_rect(rect_t a, rect_t b)
{
	if(a.x1 <= b.x2 && b.x1 <= a.x2) {
		if(a.y1 <= b.y2 && b.y1 <= a.y2) {
			return true;
		}
	}
	return false;
}

inline int get_rect_area(rect_t a)
{
	return (a.x2 - a.x1) * (a.y2 - a.y1);
}

inline int get_rect_circum(rect_t a)
{
	return 2 * ((a.x2 - a.x1) + (a.y2 - a.y1));
}

rect_t get_common_rect(rect_t a, rect_t b)
{
	rect_t ret;

	ret.x1 = MAX(a.x1, b.x1);
	ret.x2 = MIN(a.x2, b.x2);
	ret.y1 = MAX(a.y1, b.y1);
	ret.y2 = MIN(a.y2, b.y2);

	return ret;
}

int sum_rect_circum(rect_t a, int start, int sign)
{
	int ret = 0;
	int i;

	for(i = start; i < rect_num; i++) {
		rect_t c;

		if(!check_collide_rect(a, rects[i])) continue;

		c = get_common_rect(a, rects[i]);

		ret += get_rect_circum(c) * sign;

		ret += sum_rect_circum(c, i+1, -sign);
	}

	return ret;
}

int sum_rect_area(rect_t a, int start, int sign)
{
	int ret = 0;
	int i;

	for(i = start; i < rect_num; i++) {
		rect_t c;

		if(!check_collide_rect(a, rects[i])) continue;

		c = get_common_rect(a, rects[i]);

		ret += get_rect_area(c) * sign;

		ret += sum_rect_area(c, i+1, -sign);
	}

	return ret;
}

int main(void)
{
	while(1) {
		int query_type, total_area, total_circum;
		int i;

		scanf("%d%d", &rect_num, &query_type);

		if(rect_num == 0 && query_type == 0) break;

		for(i = 0; i < rect_num; i++) {
			scanf("%d%d%d%d", &(rects[i].x1), &(rects[i].y1), &(rects[i].x2), &(rects[i].y2));
		}

		total_area = 0;
		total_circum = 0;

		for(i = 0; i < rect_num; i++) {
			total_area += get_rect_area(rects[i]);
			total_area += sum_rect_area(rects[i], i+1, -1);

			if(query_type == 2) {
				total_circum += get_rect_circum(rects[i]);
				total_circum += sum_rect_circum(rects[i], i+1, -1);
			}
		}

		printf("%d\n", total_area);

		if(query_type == 2) {
			printf("%d\n", total_circum);
		}
	}

	return 0;
}

