  ( (A) > (B) ? (A) : (B) )

/** Application main entry point. */
int main (
  int     argc,
  char  * argv[ ]
  )
{
  int i, j;

  for ( ; ; )
  {
    struct
    {
      int x1;
      int y1;
      int x2;
      int y2;
    } d[ N ];
    int res = 0, rel = 0;
    int n, mode;

    scanf ( "%d%d", &n, &mode );
    if ( !( n | mode ) )  break ;

    for ( i = 0; i < n; ++i )
    {
      scanf ( "%d%d%d%d", &d[ i ].x1, &d[ i ].y1, &d[ i ].x2, &d[ i ].y2 );
    }

    for ( j = 0; j < n; ++j )
    {
      res += ( d[ j ].x2 - d[ j ].x1 ) * ( d[ j ].y2 - d[ j ].y1 );
      rel += ( d[ j ].x2 - d[ j ].x1 + d[ j ].y2 - d[ j ].y1 ) * 2;

      for ( i = j + 1; i < n; ++i )
      {
        const int w = MIN( d[ i ].x2, d[ j ].x2 )
                    - MAX( d[ i ].x1, d[ j ].x1 )
                , h = MIN( d[ i ].y2, d[ j ].y2 )
                    - MAX( d[ i ].y1, d[ j ].y1 );

        if ( w > 0 && h > 0 )
        {
          res -= w * h;
          rel -= ( w + h ) * 2;
        }
      }
    }

    if ( mode >= 1 )  printf ( "%d\n", res );
    if ( mode >= 2 )  printf ( "%d\n", rel );
  }

  return ( 0 );
}

