                        }
                        catch(ArrayIndexOutOfBoundsException e){
                            SurroundLength++;
                        }
                        try{
                            if(!Map[y][x+1]) SurroundLength++;
                        }
                        catch(ArrayIndexOutOfBoundsException e){
                            SurroundLength++;
                        }
                    }
                }
            }
            System.out.println(SurroundLength);
        }
