				area[y1][x1]++; area[y2][x1]--;
				area[y1][x2]--; area[y2][x2]++;
			}
			for (int y=1;y<MAX;y++) for (int x=1;x<MAX;x++) {
				area[y][x] += area[y][x-1];
			}
			for (int y=1;y<MAX;y++) for (int x=1;x<MAX;x++) {
				area[x][y] += area[x-1][y];
			}
//			for (int[] a:area) debug(a);
