import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

//Sheets
public class Main{

	class Scanner {
		int nextInt() {
			try {
				int c = System.in.read();
				while (c != '-' && (c < '0' || '9' < c))
					c = System.in.read();
				if (c == '-') return -nextInt();
				int res = 0;
				do {
					res *= 10;
					res += c - '0';
					c = System.in.read();
				} while ('0' <= c && c <= '9');
				return res;
			} catch (Exception e) {
				return -1;
			}
		}
	}

	class R{
		int x1, x2, type;
		public R(int x1, int x2, int type) {
			this.x1 = x1;
			this.x2 = x2;
			this.type = type;
		}
	}

	int n, r, area, len, INF = 1<<28;
	int[][] a;
	List<R>[] list;

	@SuppressWarnings("unchecked")
	void run(){
		Scanner sc = new Scanner();
		a = new int[2][10000];
		list = new List[10001];
		for(;;){
			n = sc.nextInt(); r = sc.nextInt();
			if((n|r)==0)break;
			int minx = INF, maxx = -1, miny = INF, maxy = -1;
			area = len = 0;
			for(int i=0;i<=10000;i++)list[i] = new ArrayList<R>();
			for(int i=0;i<n;i++){
				int x1 = sc.nextInt(), y1 = sc.nextInt(), x2 = sc.nextInt(), y2 = sc.nextInt();
				minx = Math.min(minx, x1);
				maxx = Math.max(maxx, x2);
				miny = Math.min(miny, y1);
				maxy = Math.max(maxy, y2);
				list[y1].add(new R(x1, x2, 1));
				list[y2].add(new R(x1, x2, -1));
			}
			Arrays.fill(a[0], 0);
			int X = 1;
			for(int y=miny;y<=maxy+1;y++,X=1-X){
				for(int x=minx;x<maxx;x++)a[X][x]=a[1-X][x];
				for(R r:list[y]){
					for(int x=r.x1;x<r.x2;x++)a[X][x]+=r.type;
				}
				for(int x=minx;x<maxx;x++){
					if(0 < a[X][x]){
						area++;
						if(x==0 || a[X][x-1]==0)len++;
						if(x==9999 || a[X][x+1]==0)len++;
					}
					if(a[X][x]==0&&0<a[1-X][x] || 0<a[X][x]&&a[1-X][x]==0)len++;
				}
			}
			System.out.println(area);
			if(r==2)System.out.println(len);
		}
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

import java.util.*;

public class Main {
	public static void main(String args[]){
		int number, judge,area_counter;
		int x1, y1,x2,y2;
		String[] coordinate = null;
		ArrayList<String> coordinateAll = new ArrayList<String>();
		Scanner scan = new Scanner(System.in);
		while(true){
			area_counter = 0;
			number = scan.nextInt();//
			judge = scan.nextInt();//1 or 2
			if(number==0 && judge ==0){
				System.exit(0);
				break;
			}
			for(int i=0;i<number;i++){
				x1 = scan.nextInt();
				y1 = scan.nextInt();
				x2 = scan.nextInt();
				y2 = scan.nextInt();
				coordinate = coordinateSet(x1,y1,x2,y2);
				for(String oneCoordinate : coordinate){
					if(!coordinateAll.contains(oneCoordinate)){
						coordinateAll.add(oneCoordinate);
						area_counter++;
					}
				}
			}
			if(judge ==1){
				coordinateAll.clear();
				System.out.println(area_counter);}
			else{
				int cf_counter = Circumference(coordinateAll);
				coordinateAll.clear();
				System.out.println(area_counter+"\n"+ cf_counter);
			}
		}
	}
	private static int Circumference(ArrayList<String> inputSet){//
		int x,y,result=0;
		for(String eachInput : inputSet){
			Scanner scan = new Scanner(eachInput);
			x = scan.nextInt();
			y = scan.nextInt();
			String checker1,checker2;
				for(int i=-1;i<2;i=i+2){
					int new_x = x+i;
					int new_y = y+i;
					checker1 = new_x + " "+y;
					if(!inputSet.contains(checker1)){
						result++;
					}
					checker2 = x + " "+ new_y;
					if(!inputSet.contains(checker2)){
						result++;
					}
				}
		}
		return result;
	}
	private static String[] coordinateSet(int x1,int y1,int x2,int y2){//
		int count =0;
		int number = Math.abs(x2-x1)*Math.abs(y2-y1);
		String[] result = new String[number];
		for(int i=x1;i<x2;i++){
			for(int j=y1;j<y2;j++){
				result[count]=i+" "+j;
				count++;
			}
		}
		return result;
	}
}

