// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=0509


public class Test
{
	public static void Main()
	{
		while(true){
		  string[] str1 = Console.ReadLine().Split(' ');
		  int n = int.Parse(str1[0]); 
		  int r = int.Parse(str1[1]); 

		  if(n == 0) break;

		  int[][] rects = new int[n][];
		  for(int i=0;i<n;i++) {
			string[] str2 = Console.ReadLine().Split(' ');
			rects[i] = Array.ConvertAll(str2, new Converter<string,int>(s => int.Parse(s)));
		  }

			int[,] brects = omit(rects);

		  //Console.WriteLine("???????????? N = {0} P = {1} Ai = {2}", n, p, a);
		  Console.WriteLine(calc(n,r,brects));


		}

	}

  public static int[,] omit(int[][] rects) {
	List<int[]> arect = new List<int[]>();
	for(int i=0;i<rects.Length;i++){
		bool insert = true;
		foreach(int[] ar in arect){
			if((ar[0] >= rects[i][0]) && (ar[1] >= rects[i][1]) && (rects[i][2] <= ar[2]) && (rects[i][3] <= ar[3])) {
				insert = false;
				break;

			}
		}
		if(insert) arect.Add(rects[i]);
	}

	  int[,] brects = new int[arect.Count,4];
	  for(int i=0;i<arect.Count;i++) {
		brects[i,0] = arect[i][0]; 
		brects[i,1] = arect[i][1]; 
		brects[i,2] = arect[i][2]; 
		brects[i,3] = arect[i][3]; 
	  }

    return brects;
  }

  public static string calc(int n, int r, int[,] rects) {
	bool[,] area = new bool[10002,10002];

	int r1 = 0;
	int r2 = 0;
	if(r==1) {
		for(int i=0;i<rects.GetLength(0);i++){
			for(int x=rects[i,0]+1;x<=rects[i,2];x++){
			for(int y=rects[i,1]+1;y<=rects[i,3];y++){
				if(area[x,y] == false) {
				  area[x,y] = true;

				  r2++;
				}
			}
			}

		}
	}
	if(r==2) {
		for(int i=0;i<rects.GetLength(0);i++){
			for(int x=rects[i,0]+1;x<=rects[i,2];x++){
			for(int y=rects[i,1]+1;y<=rects[i,3];y++){
				if(area[x,y] == false) {
				  area[x,y] = true;

				  int cnt = -4;
				  if(area[x-1,y] == false) cnt+=2;
				  if(area[x+1,y] == false) cnt+=2;
				  if(area[x,y-1] == false) cnt+=2;
				  if(area[x,y+1] == false) cnt+=2;
				  r1 += cnt;

				  r2++;
				}
			}
			}

		}
	}

	return r2.ToString() + ((r==2) ? "\n" + r1.ToString() : "");
  }



}

