  public static int[][] omit(int[][] rects) {
	//Console.WriteLine("omit1=" + rects.Length.ToString());
	Array.Sort(rects,
    (x, y) => ((y[2]-y[0])*(y[3]-y[1])).CompareTo(((x[2]-x[0])*(x[3]-x[1]))));

	List<int[]> arect = new List<int[]>();
	foreach(int[] or in rects){
		bool insert = true;
		foreach(int[] ar in arect){
			if((ar[0] <= or[0]) && (ar[1] <= or[1]) && (or[2] <= ar[2]) && (or[3] <= ar[3])) {
				insert = false;
				break;

			}
		}
		if(insert) arect.Add(or);
	}
	//Console.WriteLine("omit2=" + arect.Count.ToString());
	List<int[]> brect = new List<int[]>();
	foreach(int[] or in arect){
		int[] br = {or[0], or[1], or[2], or[3]};
		foreach(int[] ar in brect){
			if((ar[0] <= br[2]) && (br[2] <= ar[2]) && (ar[1] <= br[1]) && (br[3] <= ar[3])) {
				br[2] = ar[0];
			}
			if((br[0] <= ar[2]) && (ar[0] <= br[0]) && (ar[1] <= br[1]) && (br[3] <= ar[3])) {
				br[0] = ar[2];
			}
			if((ar[1] <= br[3]) && (br[3] <= ar[3]) && (ar[0] <= br[0]) && (br[2] <= ar[2])) {
				br[3] = ar[1];
			}
			if((br[1] <= ar[3]) && (ar[1] <= br[1]) && (ar[0] <= br[0]) && (br[2] <= ar[2])) {
				br[1] = ar[3];
			}
		}
		if((br[0]<br[2]) && (br[1]<br[3])) brect.Add(br);
	}


	//Console.WriteLine("omit3=" + brect.Count.ToString());
    return brect.ToArray();
  }

  public static string calc(int n, int r, int[][] rects) {
	bool[,] area = new bool[10002,10002];

	int r1 = 0;
	int r2 = 0;
	foreach(int[] or in rects){
		for(int x=or[0]+1;x<=or[2];x++){
		for(int y=or[1]+1;y<=or[3];y++){
			if(!area[x,y]) {
			  area[x,y] = true;

			  int cnt = -2;
			  if(!area[x-1,y]) cnt++;
			  if(!area[x+1,y]) cnt++;
			  if(!area[x,y-1]) cnt++;
			  if(!area[x,y+1]) cnt++;
			  r1 += 2*cnt;

			  r2++;
			}
		}
		}

	}

	return r2.ToString() + ((r==2) ? "\n" + r1.ToString() : "");
  }



}

