class All_Column:
	def __init__(self, rec_num):
		self.rec_num = rec_num
		self.x_stack = []
		self.y_stack = [0 for i in range(10002)]

	def add(self, x, up, down):
		if self.y_stack[x+1] == 0:
			self.x_stack.append(x)
			self.y_stack[x+1] = One_Column(x, up, down)
		else:
			self.y_stack[x+1].add(up, down)

	def calc_menseki(self):
		self.x_stack.sort()
		menseki = 0
		for i in self.x_stack:
			for j in range(len(self.y_stack[i+1].up)):
				#print(i,j)
				menseki += (self.y_stack[i+1].up[j] - self.y_stack[i+1].down[j])
			#print("x: "+str(i)+", menseki: "+str(menseki))	
		#print("menseki: "+str(menseki))
		print(str(menseki))

	def calc_shuui(self):
		shuui = 0
		for i in self.x_stack:
			#print("x:"+str(i)+", rec num:"+str(len(self.y_stack[i+1].up)))
			one_shuui = 0
			#print("up + down: "+str((len(self.y_stack[i+1].up) + len(self.y_stack[i+1].down))))
			one_shuui += (len(self.y_stack[i+1].up) + len(self.y_stack[i+1].down))
			for j in range(len(self.y_stack[i+1].up)):
				edge = 2*(self.y_stack[i+1].up[j] - self.y_stack[i+1].down[j])
				# subtract edge
				for k in [i,i+2]:	
					if self.y_stack[k] != 0:
						for l in range(len(self.y_stack[k].up)):
							if (self.y_stack[k].down[l] <= self.y_stack[i+1].down[j]) and (self.y_stack[k].up[l] < self.y_stack[i+1].up[j]) and (self.y_stack[k].up[l] > self.y_stack[i+1].down[j]):
								#print("pass 1")
								edge -= (self.y_stack[k].up[l] - self.y_stack[i+1].down[j])
							elif (self.y_stack[k].down[l] >= self.y_stack[i+1].down[j]) and (self.y_stack[k].up[l] <= self.y_stack[i+1].up[j]):	 
								#print("pass 2")
								edge -= (self.y_stack[k].up[l] - self.y_stack[k].down[l])
							elif (self.y_stack[k].down[l] > self.y_stack[i+1].down[j]) and (self.y_stack[k].up[l] >= self.y_stack[i+1].up[j]) and (self.y_stack[k].down[l] < self.y_stack[i+1].up[j]):
								#print("pass 3")
								edge -= (self.y_stack[i+1].up[j] - self.y_stack[k].down[l])
							elif (self.y_stack[k].down[l] <= self.y_stack[i+1].down[j]) and (self.y_stack[k].up[l] >= self.y_stack[i+1].up[j]):
								#print("pass 4")
								edge -= (self.y_stack[i+1].up[j] - self.y_stack[i+1].down[j]) 
				one_shuui += edge
			#print("one shuui:"+str(one_shuui))	

			shuui += one_shuui	
		#print("shuui: "+str(shuui))							
		print(str(shuui))

class One_Column:		
	def __init__(self, x, up=None, down=None):
		self.x = x
		self.up = [up]
		self.down = [down]
	def add(self, up, down):
		append_flag =True
		for i in range(len(self.up)):
			if (down <= self.down[i]) and (up >= self.up[i]):
				del self.down[i]
				del self.up[i]
			elif (down < self.down[i]) and (up >= self.down[i]):
				self.down[i] = down
				append_flag =False
			elif (down <= self.up[i]) and (up > self.up[i]):
				self.up[i] = up
				append_flag = False
			elif (down >= self.down[i]) and (up <= self.up[i]):
				append_flag = False	
		if append_flag:	
			self.up.append(up)
			self.down.append(down)

		self.up.sort()
		self.down.sort()


