int dfs(int idx)
{
	if(memo[idx] != -1) return memo[idx];

	int lt = 0, rt = 0;
	if(arr[idx] < arr[idx - 1])
		lt = dfs(idx - 1);
	if(arr[idx] < arr[idx + 1])
		rt = dfs(idx + 1);

	return memo[idx] = max(lt, rt) + (l - arr[idx]);
}

