template<typename T>bool chmax(T&a,T b){if(a<b){a=b;return true;}return false;}

int N, L;
int icicles[100002];
int memo[100002];

int rec(int i)
{
	if (memo[i] >= 0) return memo[i];

	int res = 0;

	if (icicles[i] < icicles[i + 1]) chmax(res, rec(i + 1));
	if (icicles[i] < icicles[i - 1]) chmax(res, rec(i - 1));

	res += L - icicles[i];

	return memo[i] = res;
}

signed main()
{
	memset(memo, -1, sizeof(memo));

	scanf("%d %d", &N, &L);

	rep1(i, N) scanf("%d", &icicles[i]);

	int ma = 0;

	rep1(i, N) chmax(ma, rec(i));

	printf("%d\n", ma);
}

