using namespace std;

long long int break_time[200000];
long long int length[200000];

typedef pair<int, int> P;

int main(){

	int N, L;

	cin >> N >> L;

	for(int i = 0; i <= N + 1; i++){
		break_time[i] = -1;
	}

	vector<P> v;

	for(int i = 1; i <= N; i++){
		cin >> length[i];
		P p;
		p.first = length[i];
		p.second = i;
		v.push_back(p);
	}

	sort(v.begin(), v.end());

	for(int i = N - 1; i >= 0; i--){
		P p = v[i];
		int index = p.second;
		int rest = L - length[index];
		if(break_time[index - 1] == -1 && break_time[index + 1] == -1){
			break_time[index] = rest;
		}else{
			break_time[index] = rest + max(break_time[index - 1], break_time[index + 1]);
		}
	}

	long long int ans = -1;

	for(int i = 1; i <= N; i++){
		if(ans < break_time[i]){
			ans = break_time[i];
		}
	}
	cout << ans << endl;

	return 0;
}

