int n, l, ans = 0, a[100004], start[100004];
priority_queue<pair<int,int> > longer;

int main()
{
	cin >> n >> l;
	a[0] = a[1] = a[n + 2] = a[n + 3] = 0;
	for (int i = 2; i <= n + 2; i++){
		if (i <= n + 1) cin >> a[i];
		if (a[i - 1] > a[i - 2] && a[i - 1] > a[i]){	
			pair<int, int> in = { a[i - 1], i - 1 };
			longer.push(in);
		}
	}

	while (!longer.empty()){
		pair<int, int> giveup = longer.top();
		ans = l - giveup.first + start[giveup.second];
		a[giveup.second] = 0;
		if (a[giveup.second - 1] > a[giveup.second - 2]){
			pair<int, int> in = { a[giveup.second - 1], giveup.second - 1 };
			longer.push(in);
			start[giveup.second - 1] = ans;
		}
		if (a[giveup.second + 1] > a[giveup.second + 2]){
			pair<int, int> in = { a[giveup.second + 1], giveup.second + 1 };
			longer.push(in);
			start[giveup.second + 1] = ans;
		}
		longer.pop();
	}
	cout << ans << endl;
	return 0;
}

