int N, L;
VI a, dp;
int solve(int now) {
	if (dp[now] != -1) return dp[now];
	if (a[now] == -1) return 0;

	int tmp = L - a[now];
	if (a[now - 1] < a[now] && a[now] > a[now + 1]) {
		return dp[now] = tmp;
	}

	int ret = 0;
	if (a[now - 1] > a[now]) {
		ret = solve(now - 1);
	}
	if (a[now + 1] > a[now]) {
		ret = max(ret, solve(now + 1));
	}

	return dp[now] = tmp + ret;
}

